    var _cmpGen = gen_exam1;
    var i, i_max;
    var strTbxValue = "";
    var strLabel, strValue;

    for (i = 0, i_max = arrData.length / 2; i < i_max; i++) {
        strLabel = arrData[i * 2];
        strValue = arrData[i * 2 + 1];

